import React, { Component } from "react";
import MarketAdminContract from "./contracts/MarketAdmin.json";
import getWeb3 from "./utils/getWeb3";
import truffleContract from "truffle-contract";

import "./App.css";


class App extends React.Component {

  state = { adminvalue: '',storeownervalue: '', isAdmin: false, Admins: null,StoreOwners: null,  web3: null, accounts: null, contract: null };
  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

	alert(
        `loaded accounts - ` + accounts[0]
      	);

      // Get the contract instance.
      const Contract = truffleContract(MarketAdminContract);

      Contract.setProvider(web3.currentProvider);

      const instance = await Contract.deployed();


      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.runExample);

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.` + error
      );
      console.log(error);
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;


    // Get the value from the contract to prove it worked.
    const _isAdmin = await contract.isAdmin.call(accounts[0] , { from: accounts[0] });


    // Get the value from the contract to prove it worked.
    const _Admins = await contract.getAdmins.call({ from: accounts[0] });


    // Get the value from the contract to prove it worked.
    const _StoreOwners = await contract.getStoreOwners.call({ from: accounts[0] });


	//alert(
        //`All admins are?` + _Admins
      	//);

    // Update state with the result.
    this.setState({ isAdmin: _isAdmin ,Admins: _Admins, StoreOwners: _StoreOwners});
  };


  handleChange(event) {

     this.setState({ [event.target.name]: evt.target.value });
  }

  handleSubmit(event) {

  event.preventDefault();
  const { accounts, contract } = this.state;	
  var value = this.state.adminvalue;

    alert(
        `Adding a new admin ` + value
      	);

        contract.addAdmin(value, {from : accounts[0]})
        .then(result => {         
          return contract.contract.getAdmins.call({ from: accounts[0] })
        });

	this.refs.admininput.value = '';
	this.runExample();
        this.forceUpdate(); 
  }

  handleSubmit(event) {

  event.preventDefault();
  const { accounts, contract } = this.state;	
  var value = this.state.storeownervalue;

    alert(
        `Adding a new Store Owner ` + value
      	);

        contract.addStoreOwner(value, {from : accounts[0]})
        .then(result => {         
          return contract.contract.getStoreOwner.call({ from: accounts[0] })
        });

	this.refs.storeownerinput.value = '';
	this.runExample();
        this.forceUpdate(); 
  }


  render() {
    return (
      <form onSubmit={this.handleSubmit}>
      <div className="App">
        <h1>Welcome to online MarketPlace!</h1>
	<div>Your account number is: {this.state.accounts}</div>
        <div>Is this an admin account?: {String(this.state.isAdmin)}</div>        
       </div>

	<p>
        <label>
          Add a new Admin:
          <input type="text" ref="admininput" name="adminvalue" value={this.state.adminvalue} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
	</p>
	<div id="result">List of admins: {String(this.state.Admins)}</div>

    	<p>
        <label>
          Add a new Store Owner:
          <input type="text" ref="storeownerinput" name="storeownervalue" value={this.state.storeownervalue} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
	</p>
	<div id="result">List of Store Owners: {String(this.state.StoreOwners)}</div>
      </form>

    );
  }
}
export default App;
