import React, { Component } from "react";
import truffleContract from "truffle-contract";


class StoreOwner extends React.Component {
constructor(props) {
    super(props);
    this.state = { storefrontvalue: '', storeownervalue: '', Admins: [],StoreOwners: [],PendingStores: []  , ApprovedStores: [], web3: this.props.web3, accounts: this.props.accounts, contract: this.props.contract };
    }


  componentDidMount = async () => {
  try {

      this.runExample();

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleSubmitStrOwner = this.handleSubmitStrOwner.bind(this);

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.` + error
      );
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;
    let _PendingStores = [];
    let _ApprovedStores = [];

    // Get the value from the contract to prove it worked.
    const _Admins = await contract.getAdmins.call({ from: accounts[0] });


    // Get the value from the contract to prove it worked.
    const _StoreOwners = await contract.getStoreOwners.call({ from: accounts[0] });

    // Get total storefront count from the contract.
    const _StoreFrontIds = await contract.getStoreFrontIds.call({ from: accounts[0] });

    for (let userObject of _StoreFrontIds) {
	alert(
        `Your store IDs are:` + userObject
      );
      // Get the value from the contract to prove it worked.
      const _StoreFront = await contract.getStoreFrontbyId.call(userObject, { from: accounts[0] });

	//if(_StoreFront[3])
	  _ApprovedStores.push(_StoreFront[0] );	

         //Create the parent and add the children
         _PendingStores.push(_StoreFront[0] );

    }

    // Update state with the result.
    this.setState({ storefrontvalue: '',storeownervalue: '',Admins: _Admins, StoreOwners: _StoreOwners,PendingStores: _PendingStores, ApprovedStores: _ApprovedStores});

  };


  handleChange(event) {

     this.setState({ [event.target.name]: event.target.value });
  }

  handleSubmit(event) {

  event.preventDefault();
  const { accounts, contract } = this.state;	
  var value = this.state.storefrontvalue;

    alert(
        `Adding a new Store Front ` + value
      	);

        contract.createStoreFront(value, {from : accounts[0]})
        .then(result => {  		
		this.runExample();
        	this.forceUpdate(); 
        });
	this.setState({ storefrontvalue: '',storeownervalue: ''});
  }

  handleSubmitStrOwner(event) {

  event.preventDefault();
  const { accounts, contract } = this.state;	
  var value = this.state.storeownervalue;

    alert(
        `Adding a new Store Owner ` + value
      	);

        contract.addStoreOwner(value, {from : accounts[0]})
        .then(result => {         
		this.runExample();
        	this.forceUpdate(); 
        });
	this.setState({ storefrontvalue: '',storeownervalue: ''});

  }



  render() {

    var storeownerlist = this.state.StoreOwners;
    var adminlist = this.state.Admins;
    var pendingstorelist = this.state.PendingStores;
    var approvedstorelist = this.state.ApprovedStores;

    return ( 	
      <form onSubmit={this.handleSubmit.bind(this)}>

       <h3>You are logged in as STORE OWNER</h3>
       <p>
        <label>
          Add a new Store Front:
          <input type="text" ref="storefrontinput" name="storefrontvalue" value={this.state.storefrontvalue} onChange={this.handleChange} placeholder="Enter Store Name i.e. Walmart..."/>
        </label>
        <button type="submit">Add</button>
	</p>
	<ul> 	
		<div id="result">List of Store Fronts:</div>
                {pendingstorelist.map(function(pendingstorelist, index){
                    return <li key={ index }>{pendingstorelist}</li>;
                  })}
            </ul>       
	 <br/>

    	<p>
        <label>
          Manage Approved Stores:
          <input type="text" ref="storeownerinput" name="storeownervalue" value={this.state.storeownervalue} onChange={this.handleChange} placeholder="Enter Store Owner Address..."/>
        </label>
        <button onClick={this.handleSubmitStrOwner.bind(this)}>Add</button>
	</p>
	   <ul> 	
		<div id="result">List of Store Owners:</div>
                {approvedstorelist.map(function(approvedstorelist, index){
                    return <li key={ index }>{approvedstorelist}</li>;
                  })}
            </ul>       
	 <br/>


    	<p>
        <label>
          Withdraw Store Balance: 
          <input type="text" ref="storeownerinput" name="storeownervalue" value={this.state.storeownervalue} onChange={this.handleChange} placeholder="Enter Store Number i.e 23..."/>
        </label>
        <button onClick={this.handleSubmitStrOwner.bind(this)}>Add</button>
	</p>
	<ul> 	
		<div id="result">List of Pending Store Openings:</div>
                {pendingstorelist.map(function(pendingstorelist, index){
                    return <li key={ index }>{pendingstorelist}</li>;
                  })}
            </ul>       
	 <br/>


      </form>	
    );
  }
}
export default StoreOwner;
