import React, { Component } from "react";
import MarketAdminContract from "./contracts/MarketAdmin.json";
import getWeb3 from "./utils/getWeb3";
import truffleContract from "truffle-contract";

import "./App.css";

class App extends Component {
  state = { isAdmin: 0, web3: null, accounts: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();


	alert(
        `loaded accounts` + accounts
      	);

      // Get the contract instance.
      const Contract = truffleContract(MarketAdminContract);

      Contract.setProvider(web3.currentProvider);

      const instance = await Contract.deployed();



      alert(
        `deployed`
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.runExample);



    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`
      );
      console.log(error);
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.isAdmin(accounts[0], { from: accounts[0] });

    // Get the value from the contract to prove it worked.
    const response = await contract.get();

    // Update state with the result.
    this.setState({ isAdmin: response });
  };

handleClick(event)
{
  const { accounts, contract } = this.state;

  var value = 302

    alert(
        `Setting storage value to ` + value
      	);

    alert(
        `Account is  ` + accounts[0]
      	);


        contract.set(value, {from : accounts[0]})
        .then(result => {         
          return contract.get.call()
        })
        .then(result => {      
	   this.setState({ isAdmin: result.toNumber() }); 
        })
}

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>Welcome to online MarketPlace!</h1>
 	<p>
	   <button onClick={this.handleClick.bind(this)}>Set Storage</button>
	</p>
        <div>The stored value is: {this.state.isAdmin}</div>
      </div>
    );
  }
}

export default App;
