import React, { Component } from "react";
import MarketAdminContract from "./contracts/MarketAdmin.json";
import getWeb3 from "./utils/getWeb3";
import truffleContract from "truffle-contract";

import "./App.css";


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: '',isAdmin: false, web3: null, accounts: null, contract: null };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

	alert(
        `loaded accounts - ` + accounts[0]
      	);

      // Get the contract instance.
      const Contract = truffleContract(MarketAdminContract);

      Contract.setProvider(web3.currentProvider);

      const instance = await Contract.deployed();


      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: instance }, this.runExample);

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.` + error
      );
      console.log(error);
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;

	//alert(
        //`Adding a new admin - ` + '0x83ce6378f3d863f90c279e960550daa12bbba59b'
      	//);

    // Stores a given value, 5 by default.
    //await contract.addAdmin('0x83ce6378f3d863f90c279e960550daa12bbba59b', { from: accounts[0] });

	alert(
        `Admin added successfully`
      	);

    // Get the value from the contract to prove it worked.
    const response = await contract.isAdmin.call(accounts[0] , { from: accounts[0] });

	alert(
        `IsAdmin?` + response
      	);

    // Update state with the result.
    this.setState({ isAdmin: response });
  };


  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {

  event.preventDefault();
  const { accounts, contract } = this.state;	
  var value = this.state.value;

    alert(
        `Adding a new admin ` + value
      	);

        contract.addAdmin(value, {from : accounts[0]})
        .then(result => {         
          return contract.isAdmin.call(value, {from : accounts[0]})
        })
        .then(result => {      
	   this.setState({ isAdmin: result }); 
        })

  }

handleClick(event)
{
  const { accounts, contract } = this.state;

  var value = 302

    alert(
        `Setting storage value to ` + value
      	);

    alert(
        `Account is  ` + accounts[0]
      	);


        contract.set(value, {from : accounts[0]})
        .then(result => {         
          return contract.get.call()
        })
        .then(result => {      
	   this.setState({ isAdmin: result.toNumber() }); 
        })
}

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
	<div className="App">
        <h1>Welcome to online MarketPlace!</h1>
 	<p>
	   <button onClick={this.handleClick.bind(this)}>Set Storage</button>
	</p>
	<div>Your account number is?: {this.state.isAdmin}</div>
        <div>Is this admin?: {this.state.isAdmin}</div>
      </div>
        <label>
          Add a new Admin:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}
export default App;
