import React, { Component } from "react";
import truffleContract from "truffle-contract";


class Shopper extends React.Component {
constructor(props) {
    super(props);

    this.state = { storefrontvalue: [] ,ApprovedStores: [], SelectList: [] ,ProductList: [], web3: this.props.web3, accounts: this.props.accounts, contract: this.props.contract };

    alert(
        `Account no is ` + this.state.accounts
      	);    
   }


  componentDidMount = async () => {
  try {

      this.runExample();

      this.handleSubmit = this.handleSubmit.bind(this);

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.` + error
      );
    }
  };

  runExample = async () => {
    const { accounts, contract } = this.state;
    let _PendingStores = [];
    let _ApprovedStores = [];
    let _SelectList = [];
    let counter = 0 


    // Get all storefront ids from the contract.
    const _StoreFrontIds = await contract.getStoreFrontIds.call({ from: accounts[0] });
    alert(
        `Total store count is:` + accounts[0]
    );

    for (let userObject of _StoreFrontIds) {

      // Get the value from the contract to prove it worked.
      const _StoreFront = await contract.getStoreFrontbyId.call(userObject, { from: accounts[0] });

	if(_StoreFront[3])
        {
	  if(counter == 0)
          {
		this.state.selectstorevalue = _StoreFront[0];
                counter += 1;
           }
	  _ApprovedStores.push(_StoreFront[0] );	
          _SelectList.push({Name:_StoreFront[0] ,Id: userObject });	
        }

         //Create the parent and add the children
         _PendingStores.push(_StoreFront[0] );


    }

    // Update state with the result.
    this.setState({  ApprovedStores: _ApprovedStores, SelectList: _SelectList, ProductList: []});
    this.RefreshProductList();
  };


  handleSubmit(event) {

  event.preventDefault();
  const { accounts, contract } = this.state;	
  var value = this.state.storefrontvalue;

    alert(
        `Adding a new Store Front ` + value
      	);

        contract.createStoreFront(value, {from : accounts[0]})
        .then(result => {  		
		this.runExample();
        	this.forceUpdate(); 
        });
	this.setState({ storefrontvalue: ''});
  }



  render() {



    return ( 	
      <form onSubmit={this.handleSubmit.bind(this)}>

       <h3>You are logged in as SHOPPER</h3>

	</form>	
    );
  }
}
export default Shopper;
